import React, { Component } from 'react';
import PropTypes from 'prop-types'
import './App.css';


class Form extends Component{

  constructor(props) {
    super(props);
    this.state = {
      firstname: "Firstname",
      lastname: "Lastname",
      buttons: <button onClick={this.edit}>Edit</button>
    };
    //this.handleFNameChange = this.handleFNameChange.bind(this);
    //this.handleLNameChange = this.handleLNameChange.bind(this);
  }

  edit = () => {
    this.setState({
      firstname: <input type ='text' value={this.state.firstname} onChange={this.handleFNameChange}/>,
      lastname: <input type ='text' value={this.state.lastname} onChange={this.handleLNameChange}/>
    });
    document.getElementById("buttons").innerHTML = "<button onClick={this.submit}>Submit</button>"
  }

  submit = () => {
    this.setState({
      firstname: this.state.firstname,
      lastname: this.state.lastname
    })
  }
  render(){
    return <div>
      <div id='number'><h2>{this.state.num}</h2></div> 
      <div id="firstname">
        <h2>{this.state.firstname}</h2>
      </div>

      <div id="lastname">
        <h2>{this.state.lastname}</h2>
      </div>
      <div id="buttons">
      {/* <button onClick={this.edit}>Edit</button> */}
      {this.state.buttons}
      </div>
    </div>
  }
  handleFNameChange = (event) => {
    this.setState({
      firstname: <input type ='text' value={event.target.value} onChange={this.handleFNameChange}/>
    });
  }

  handleLNameChange = (event) => {
    this.setState({
      lastname: <input type ='text' value={event.target.value} onChange={this.handleLNameChange}/>
    });
  }
};


Form.propTypes = {
  firstname: PropTypes.string.isRequired,
  lastname: PropTypes.string.isRequired
}

function App() {
  return (
    <div className="app">

    <Form />

    </div>


  );
}

export default App;
